function createValidatedObject(schema) {
  return new Proxy({}, {
    set(target, prop, value) {
      if (schema[prop] && !schema[prop](value)) {
        throw new TypeError(`Invalid value for ${prop}`);
      }
      target[prop] = value;
      return true;
    },
    get(target, prop) {
      return target[prop];
    }
  });
}

