exercises.solution:
class PromisePool {
  constructor(concurrency) {
    this._concurrency = concurrency;
    this._active = 0;
    this._completed = 0;
    this._failed = 0;
    this._queue = [];
    this._drainResolvers = [];
  }

  add(taskFn) {
    return new Promise((resolve, reject) => {
      const task = { fn: taskFn, resolve, reject };
      if (this._active < this._concurrency) {
        this._run(task);
      } else {
        this._queue.push(task);
      }
    });
  }

  async _run(task) {
    this._active++;
    try {
      const result = await task.fn();
      this._completed++;
      task.resolve(result);
    } catch (err) {
      this._completed++;
      this._failed++;
      task.reject(err);
    } finally {
      this._active--;
      if (this._queue.length > 0) {
        this._run(this._queue.shift());
      } else if (this._active === 0) {
        for (const resolver of this._drainResolvers) resolver();
        this._drainResolvers = [];
      }
    }
  }

  async drain() {
    if (this._active === 0 && this._queue.length === 0) return;
    return new Promise(resolve => this._drainResolvers.push(resolve));
  }

  get active() { return this._active; }
  get pending() { return this._queue.length; }
  get completed() { return this._completed; }
  get failed() { return this._failed; }
}

