exercises.solution:
class Order {
  constructor(id, items) {
    this.id = id;
    this.items = items;
    this.status = 'created';
  }
  get total() {
    return this.items.reduce((sum, item) => sum + item.price, 0);
  }
  async transition(newStatus) {
    const valid = { created: 'paid', paid: 'shipped', shipped: 'delivered' };
    if (valid[this.status] !== newStatus && !(this.status === 'paid' && newStatus === 'created')) {
      throw new Error(`Invalid transition: ${this.status} → ${newStatus}`);
    }
    this.status = newStatus;
  }
}

class Payment {
  constructor(order) {
    this.order = order;
  }
  async process() {
    if (this.order.total <= 0) throw new Error('Invalid total');
    if (this.order.status !== 'created') throw new Error('Order not in created state');
    await this.order.transition('paid');
    return { success: true, orderId: this.order.id };
  }
  async refund() {
    if (this.order.status !== 'paid') throw new Error('Order not in paid state');
    await this.order.transition('created');
    return { refunded: true };
  }
}

class ShippingManager {
  constructor() {
    this._history = [];
  }
  async ship(order) {
    if (order.status !== 'paid') throw new Error('Order not paid');
    await order.transition('shipped');
    this._history.push({ orderId: order.id, action: 'ship', timestamp: Date.now() });
    return { shipped: true, trackingId: `TRK-${order.id}` };
  }
  async deliver(order) {
    if (order.status !== 'shipped') throw new Error('Order not shipped');
    await order.transition('delivered');
    this._history.push({ orderId: order.id, action: 'deliver', timestamp: Date.now() });
    return { delivered: true };
  }
  get history() { return this._history; }
}

