exercises.solution:
function createPaginatedFetcher(fetchPage) {
  return {
    async *[Symbol.asyncIterator]() {
      let page = 1;
      let hasMore = true;
      while (hasMore) {
        const result = await fetchPage(page);
        for (const item of result.data) {
          yield item;
        }
        hasMore = result.hasMore;
        page++;
      }
    }
  };
}

