exercises.solution:
function createPipeline() {
  const steps = [];

  return {
    step(name, processFn, options = {}) {
      steps.push({
        name,
        processFn,
        fallback: options.fallback || null,
        required: options.required !== false,
      });
      return this;
    },
    async execute(initialData) {
      let data = initialData;
      const report = [];
      let stopped = false;

      for (const step of steps) {
        if (stopped) {
          report.push({ name: step.name, status: 'skipped', duration: 0 });
          continue;
        }
        const start = Date.now();
        try {
          data = await step.processFn(data);
          report.push({ name: step.name, status: 'success', duration: Date.now() - start });
        } catch (err) {
          if (step.fallback) {
            try {
              data = await step.fallback(err, data);
              report.push({ name: step.name, status: 'recovered', duration: Date.now() - start });
            } catch (fallbackErr) {
              report.push({ name: step.name, status: 'failed', duration: Date.now() - start });
              if (step.required) stopped = true;
            }
          } else {
            report.push({ name: step.name, status: 'failed', duration: Date.now() - start });
            if (step.required) stopped = true;
          }
        }
      }

      return { result: data, steps: report };
    }
  };
}

