exercises.solution:
class CircuitBreaker {
  constructor(options = {}) {
    this._failureThreshold = options.failureThreshold ?? 3;
    this._cooldownMs = options.cooldownMs ?? 5000;
    this._successThreshold = options.successThreshold ?? 1;
    this._state = 'closed';
    this._failures = 0;
    this._successes = 0;
    this._openedAt = null;
  }

  async call(fn) {
    if (this._state === 'open') {
      if (Date.now() - this._openedAt >= this._cooldownMs) {
        this._state = 'half-open';
      } else {
        throw new Error('Circuit is open');
      }
    }

    try {
      const result = await fn();
      this._successes++;
      if (this._state === 'half-open') {
        if (this._successes >= this._successThreshold) {
          this._state = 'closed';
          this._failures = 0;
        }
      } else {
        this._failures = 0;
      }
      return result;
    } catch (err) {
      this._failures++;
      this._successes = 0;
      if (this._failures >= this._failureThreshold || this._state === 'half-open') {
        this._state = 'open';
        this._openedAt = Date.now();
      }
      throw err;
    }
  }

  get state() { return this._state; }
  get stats() { return { failures: this._failures, successes: this._successes, state: this._state }; }
  reset() { this._state = 'closed'; this._failures = 0; this._successes = 0; this._openedAt = null; }
}

