exercises.solution:
class Observable {
  constructor(subscribeFn) {
    this._subscribeFn = subscribeFn;
  }

  subscribe(observer) {
    const cleanup = this._subscribeFn(observer);
    return { unsubscribe: cleanup || (() => {}) };
  }

  map(fn) {
    return new Observable((observer) => {
      const sub = this.subscribe({
        next: (val) => observer.next(fn(val)),
        error: (err) => observer.error && observer.error(err),
        complete: () => observer.complete && observer.complete(),
      });
      return () => sub.unsubscribe();
    });
  }

  filter(fn) {
    return new Observable((observer) => {
      const sub = this.subscribe({
        next: (val) => { if (fn(val)) observer.next(val); },
        error: (err) => observer.error && observer.error(err),
        complete: () => observer.complete && observer.complete(),
      });
      return () => sub.unsubscribe();
    });
  }

  static fromArray(arr) {
    return new Observable((observer) => {
      for (const item of arr) observer.next(item);
      if (observer.complete) observer.complete();
    });
  }

  static interval(ms) {
    return new Observable((observer) => {
      let count = 0;
      const id = setInterval(() => observer.next(count++), ms);
      return () => clearInterval(id);
    });
  }
}

class Subject {
  constructor() {
    this._observers = [];
    this._completed = false;
  }

  next(value) {
    if (this._completed) return;
    for (const obs of this._observers) obs.next(value);
  }

  error(err) {
    for (const obs of this._observers) obs.error && obs.error(err);
  }

  complete() {
    this._completed = true;
    for (const obs of this._observers) obs.complete && obs.complete();
  }

  subscribe(observer) {
    this._observers.push(observer);
    return { unsubscribe: () => {
      this._observers = this._observers.filter(o => o !== observer);
    }};
  }
}

