exercises.solution:
class AsyncLock {
  constructor() {
    this._locked = false;
    this._queue = [];
  }
  async acquire() {
    if (!this._locked) {
      this._locked = true;
      return;
    }
    return new Promise(resolve => this._queue.push(resolve));
  }
  release() {
    if (this._queue.length > 0) {
      const next = this._queue.shift();
      next();
    } else {
      this._locked = false;
    }
  }
  get locked() { return this._locked; }
}

class ResourcePool {
  constructor(factory, size) {
    this._resources = Array.from({ length: size }, () => factory());
    this._inUse = new Set();
    this._queue = [];
  }
  async acquire(timeoutMs) {
    if (this._resources.length > 0) {
      const resource = this._resources.pop();
      this._inUse.add(resource);
      return resource;
    }
    return new Promise((resolve, reject) => {
      const entry = { resolve, reject, timer: null };
      if (timeoutMs !== undefined) {
        entry.timer = setTimeout(() => {
          this._queue = this._queue.filter(e => e !== entry);
          reject(new Error('Acquire timeout'));
        }, timeoutMs);
      }
      this._queue.push(entry);
    });
  }
  release(resource) {
    if (!this._inUse.has(resource)) return;
    this._inUse.delete(resource);
    if (this._queue.length > 0) {
      const entry = this._queue.shift();
      if (entry.timer) clearTimeout(entry.timer);
      this._inUse.add(resource);
      entry.resolve(resource);
    } else {
      this._resources.push(resource);
    }
  }
  get available() { return this._resources.length; }
  get inUse() { return this._inUse.size; }
}

