}

exercises.solution:
function createServiceLocator() {
  const factories = {};
  const instances = {};
  return {
    register: function(name, factory) {
      factories[name] = factory;
    },
    get: function(name) {
      if (!factories[name]) return undefined;
      if (!instances.hasOwnProperty(name)) {
        instances[name] = factories[name]();
      }
      return instances[name];
    },
    has: function(name) {
      return factories.hasOwnProperty(name);
    },
    getAll: function() {
      const all = {};
      for (const name in factories) {
        all[name] = this.get(name);
      }
      return all;
    }
  };
}

