  constructor() {
    // your code here
  }

  on(event, callback) {}
  off(event, callback) {}
  emit(event, ...args) {}
}

exercises.solution:
class EventEmitter {
  constructor() {
    this._events = {};
  }

  on(event, callback) {
    if (!this._events[event]) this._events[event] = [];
    this._events[event].push(callback);
  }

  off(event, callback) {
    if (!this._events[event]) return;
    this._events[event] = this._events[event].filter(function(cb) { return cb !== callback; });
  }

  emit(event, ...args) {
    if (!this._events[event]) return;
    this._events[event].forEach(function(cb) { cb.apply(null, args); });
  }
}

