    const c = new RC(100);
    c.set('/a', 'dataA');
    const r1 = c.get('/a') === 'dataA' && c.has('/a') === true;
    fakeTime += 50;
    const r2 = c.get('/a') === 'dataA';
    fakeTime += 60;
    const r3 = c.get('/a') === null && c.has('/a') === false;
    fakeTime = origNow.call(Date);
    const c2 = new RC(1000);
    c2.set('/x', 'x1');
    c2.set('/y', 'y1');
    const r4sz = c2.size === 2;
    c2.clear();
    const r4 = c2.size === 0;
