  const stripped = input.replace(/ /g, "");
  if (stripped.length <= 1 || /[^0-9]/.test(stripped)) return false;
  let sum = 0;
  let double = false;
  for (let i = stripped.length - 1; i >= 0; i--) {
    let digit = Number(stripped[i]);
    if (double) {
      digit *= 2;
      if (digit > 9) digit -= 9;
    }
    sum += digit;
    double = !double;
  }
  return sum % 10 === 0;
}

