exercises.solution:
function translate(sentence) {
  return sentence.split(" ").map(word => {
    if (/^([aeiou]|xr|yt)/i.test(word)) return word + "ay";
    const match = word.match(/^([^aeiou]*qu|[^aeiou]+)/i);
    if (match) return word.slice(match[1].length) + match[1] + "ay";
    return word + "ay";
  }).join(" ");
}

