exercises.solution:
class GradeSchool {
  constructor() {
    this._roster = {};
  }

  add(name, grade) {
    for (const g in this._roster) {
      this._roster[g] = this._roster[g].filter(s => s !== name);
    }
    if (!this._roster[grade]) this._roster[grade] = [];
    this._roster[grade].push(name);
    this._roster[grade].sort();
  }

  grade(gradeNum) {
    return [...(this._roster[gradeNum] || [])];
  }

  roster() {
    const result = {};
    for (const g of Object.keys(this._roster).sort((a, b) => a - b)) {
      result[g] = [...this._roster[g]];
    }
    return result;
  }
}

