exercises.solution:
class Allergies {
  constructor(score) {
    this.score = score;
    this.allergens = [
      "eggs", "peanuts", "shellfish", "strawberries",
      "tomatoes", "chocolate", "pollen", "cats"
    ];
  }

  list() {
    return this.allergens.filter((_, i) => this.score & (1 << i));
  }

  allergicTo(item) {
    const idx = this.allergens.indexOf(item);
    return idx !== -1 && (this.score & (1 << idx)) !== 0;
  }
}

