exercises.solution:
function classify(number) {
  if (number < 1) throw new Error("Classification is only possible for positive integers");
  let sum = 0;
  for (let i = 1; i <= Math.floor(number / 2); i++) {
    if (number % i === 0) sum += i;
  }
  if (sum === number) return "perfect";
  if (sum > number) return "abundant";
  return "deficient";
}

