exercises.solution:
function convert(digits, inputBase, outputBase) {
  if (inputBase < 2 || Math.floor(inputBase) !== inputBase) throw new Error("Wrong input base");
  if (outputBase < 2 || Math.floor(outputBase) !== outputBase) throw new Error("Wrong output base");
  if (digits.length === 0) throw new Error("Input has wrong format");
  if (digits.length > 1 && digits[0] === 0) throw new Error("Input has wrong format");
  for (const d of digits) {
    if (d < 0 || d >= inputBase || Math.floor(d) !== d) throw new Error("Input has wrong format");
  }
  let value = digits.reduce((acc, d) => acc * inputBase + d, 0);
  if (value === 0) return [0];
  const result = [];
  while (value > 0) {
    result.unshift(value % outputBase);
    value = Math.floor(value / outputBase);
  }
  return result;
}

