exercises.solution:
function encode(text) {
  const transformed = text.toLowerCase().replace(/[^a-z0-9]/g, "").split("").map(ch => {
    if (ch >= "a" && ch <= "z") return String.fromCharCode(219 - ch.charCodeAt(0));
    return ch;
  }).join("");
  return transformed.match(/.{1,5}/g).join(" ");
}

function decode(text) {
  return text.replace(/ /g, "").split("").map(ch => {
    if (ch >= "a" && ch <= "z") return String.fromCharCode(219 - ch.charCodeAt(0));
    return ch;
  }).join("");
}

