exercises.solution:
class Bowling {
  constructor() {
    this.rolls = [];
  }

  roll(pins) {
    if (pins < 0 || pins > 10) throw new Error("Invalid roll");
    this.rolls.push(pins);
  }

  score() {
    let total = 0;
    let rollIdx = 0;
    for (let frame = 0; frame < 10; frame++) {
      if (rollIdx >= this.rolls.length) throw new Error("Game is not complete");
      if (this.rolls[rollIdx] === 10) {
        total += 10 + (this.rolls[rollIdx + 1] || 0) + (this.rolls[rollIdx + 2] || 0);
        rollIdx++;
      } else {
        const first = this.rolls[rollIdx];
        const second = this.rolls[rollIdx + 1];
        if (second === undefined) throw new Error("Game is not complete");
        if (first + second > 10) throw new Error("Invalid frame");
        if (first + second === 10) {
          total += 10 + (this.rolls[rollIdx + 2] || 0);
        } else {
          total += first + second;
        }
        rollIdx += 2;
      }
    }
    return total;
  }
}

