exercises.solution:
class LinkedList {
  constructor() {
    this.head = null;
    this._size = 0;
  }

  push(value) {
    this.head = { value, next: this.head };
    this._size++;
  }

  pop() {
    if (!this.head) throw new Error("Empty list");
    const val = this.head.value;
    this.head = this.head.next;
    this._size--;
    return val;
  }

  size() {
    return this._size;
  }

  toArray() {
    const arr = [];
    let node = this.head;
    while (node) {
      arr.push(node.value);
      node = node.next;
    }
    return arr;
  }

  reverse() {
    let prev = null;
    let current = this.head;
    while (current) {
      const next = current.next;
      current.next = prev;
      prev = current;
      current = next;
    }
    this.head = prev;
  }
}

