exercises.solution:
class CustomSet {
  constructor(values = []) {
    this.data = [];
    for (const v of values) this.add(v);
  }

  contains(value) {
    return this.data.includes(value);
  }

  add(value) {
    if (!this.contains(value)) this.data.push(value);
    return this;
  }

  empty() {
    return this.data.length === 0;
  }

  size() {
    return this.data.length;
  }

  isSubset(other) {
    return this.data.every(v => other.contains(v));
  }

  isDisjoint(other) {
    return this.data.every(v => !other.contains(v));
  }

  isEqual(other) {
    return this.isSubset(other) && other.isSubset(this);
  }

  union(other) {
    const result = new CustomSet(this.data);
    for (const v of other.data) result.add(v);
    return result;
  }

  intersection(other) {
    return new CustomSet(this.data.filter(v => other.contains(v)));
  }

  difference(other) {
    return new CustomSet(this.data.filter(v => !other.contains(v)));
  }
}

