exercises.solution:
class CircularBuffer {
  constructor(capacity) {
    this.capacity = capacity;
    this.buffer = [];
  }

  read() {
    if (this.buffer.length === 0) throw new Error("Buffer is empty");
    return this.buffer.shift();
  }

  write(value) {
    if (this.buffer.length >= this.capacity) throw new Error("Buffer is full");
    this.buffer.push(value);
  }

  overwrite(value) {
    if (this.buffer.length >= this.capacity) {
      this.buffer.shift();
    }
    this.buffer.push(value);
  }

  clear() {
    this.buffer = [];
  }
}

