exercises.solution:
function saddlePoints(matrix) {
  if (matrix.length === 0) return [];
  const results = [];
  for (let r = 0; r < matrix.length; r++) {
    for (let c = 0; c < matrix[0].length; c++) {
      const val = matrix[r][c];
      const isRowMax = matrix[r].every(v => val >= v);
      const isColMin = matrix.every(row => val <= row[c]);
      if (isRowMax && isColMin) {
        results.push({ row: r + 1, column: c + 1 });
      }
    }
  }
  return results;
}

