exercises.solution:
class QueenAttack {
  constructor({ white = [0, 3], black = [7, 3] } = {}) {
    if (white[0] === black[0] && white[1] === black[1]) throw new Error("Queens cannot share the same space");
    this.white = white;
    this.black = black;
  }

  canAttack() {
    const [wr, wc] = this.white;
    const [br, bc] = this.black;
    return wr === br || wc === bc || Math.abs(wr - br) === Math.abs(wc - bc);
  }
}

