exercises.solution:
function tournamentTally(input) {
  const teams = {};
  function ensure(name) {
    if (!teams[name]) teams[name] = { mp: 0, w: 0, d: 0, l: 0, p: 0 };
  }
  if (input.trim() === "") {
    return "Team                           | MP |  W |  D |  L |  P";
  }
  input.split("\n").forEach(line => {
    const [t1, t2, result] = line.split(";");
    ensure(t1); ensure(t2);
    teams[t1].mp++; teams[t2].mp++;
    if (result === "win") {
      teams[t1].w++; teams[t1].p += 3;
      teams[t2].l++;
    } else if (result === "loss") {
      teams[t2].w++; teams[t2].p += 3;
      teams[t1].l++;
    } else {
      teams[t1].d++; teams[t1].p += 1;
      teams[t2].d++; teams[t2].p += 1;
    }
  });
  const sorted = Object.keys(teams).sort((a, b) => teams[b].p - teams[a].p || a.localeCompare(b));
  const header = "Team                           | MP |  W |  D |  L |  P";
  const rows = sorted.map(name => {
    const t = teams[name];
    return name.padEnd(31) + "|" + String(t.mp).padStart(3) + " |" + String(t.w).padStart(3) + " |" + String(t.d).padStart(3) + " |" + String(t.l).padStart(3) + " |" + String(t.p).padStart(3);
  });
  return [header, ...rows].join("\n");
}

