exercises.solution:
function gamestate(board) {
  const lines = [
    [[0,0],[0,1],[0,2]], [[1,0],[1,1],[1,2]], [[2,0],[2,1],[2,2]],
    [[0,0],[1,0],[2,0]], [[0,1],[1,1],[2,1]], [[0,2],[1,2],[2,2]],
    [[0,0],[1,1],[2,2]], [[0,2],[1,1],[2,0]]
  ];
  function at(r, c) { return board[r][c]; }
  function wins(ch) {
    return lines.some(line => line.every(([r, c]) => at(r, c) === ch));
  }
  const xCount = board.join("").split("").filter(c => c === "X").length;
  const oCount = board.join("").split("").filter(c => c === "O").length;
  const xWins = wins("X");
  const oWins = wins("O");
  if (xWins && oWins) throw new Error("Invalid board");
  if (xCount < oCount || xCount > oCount + 1) throw new Error("Invalid board");
  if (xWins && oCount === xCount) throw new Error("Invalid board");
  if (oWins && xCount > oCount) throw new Error("Invalid board");
  if (xWins || oWins) return "win";
  if (xCount + oCount === 9) return "draw";
  return "ongoing";
}

