class Robot {
  constructor() {
    this.x = 0;
    this.y = 0;
    this.dir = "north";
  }

  get bearing() { return this.dir; }
  get coordinates() { return [this.x, this.y]; }

  orient(direction) {
    this.dir = direction;
  }

  at(x, y) {
    this.x = x;
    this.y = y;
  }

  turnRight() {
    const dirs = ["north", "east", "south", "west"];
    this.dir = dirs[(dirs.indexOf(this.dir) + 1) % 4];
  }

  turnLeft() {
    const dirs = ["north", "east", "south", "west"];
    this.dir = dirs[(dirs.indexOf(this.dir) + 3) % 4];
  }

  advance() {
    if (this.dir === "north") this.y++;
    else if (this.dir === "south") this.y--;
    else if (this.dir === "east") this.x++;
    else if (this.dir === "west") this.x--;
  }

  instructions(str) {
    for (const ch of str) {
      if (ch === "L") this.turnLeft();
      else if (ch === "R") this.turnRight();
