exercises.solution:
function say(number) {
  if (number < 0 || number >= 1e12) throw new Error("Number must be between 0 and 999,999,999,999");
  if (number === 0) return "zero";
  const ones = ["", "one", "two", "three", "four", "five", "six", "seven", "eight", "nine",
    "ten", "eleven", "twelve", "thirteen", "fourteen", "fifteen", "sixteen", "seventeen", "eighteen", "nineteen"];
  const tens = ["", "", "twenty", "thirty", "forty", "fifty", "sixty", "seventy", "eighty", "ninety"];
  function chunk(n) {
    if (n === 0) return "";
    if (n < 20) return ones[n];
    if (n < 100) return tens[Math.floor(n / 10)] + (n % 10 ? "-" + ones[n % 10] : "");
    return ones[Math.floor(n / 100)] + " hundred" + (n % 100 ? " " + chunk(n % 100) : "");
  }
  const scales = ["", " thousand", " million", " billion"];
  const parts = [];
  let scaleIdx = 0;
  while (number > 0) {
    const rem = number % 1000;
    if (rem > 0) parts.unshift(chunk(rem) + scales[scaleIdx]);
    number = Math.floor(number / 1000);
    scaleIdx++;
  }
  return parts.join(" ");
}

