  // your code here
}

exercises.solution:
function answer(question) {
  const cleaned = question.replace("What is", "").replace("?", "").trim();
  if (!cleaned) throw new Error("Invalid question");
  const tokens = cleaned.split(/\s+/);
  let result = Number(tokens[0]);
  if (isNaN(result)) throw new Error("Invalid question");
  let i = 1;
  while (i < tokens.length) {
    let op;
    if (tokens[i] === "plus") { op = "+"; i++; }
    else if (tokens[i] === "minus") { op = "-"; i++; }
    else if (tokens[i] === "multiplied" && tokens[i + 1] === "by") { op = "*"; i += 2; }
    else if (tokens[i] === "divided" && tokens[i + 1] === "by") { op = "/"; i += 2; }
    else throw new Error("Unknown operation");
    const num = Number(tokens[i]);
    if (isNaN(num)) throw new Error("Invalid question");
    if (op === "+") result += num;
    else if (op === "-") result -= num;
    else if (op === "*") result *= num;
    else if (op === "/") result /= num;
    i++;
  }
  return result;
}

