exercises.solution:
function largestProduct(digits, span) {
  if (span < 0) throw new Error("Span must not be negative");
  if (span > digits.length) throw new Error("Span must be smaller than string length");
  if (/[^0-9]/.test(digits)) throw new Error("Digits input must only contain digits");
  if (span === 0) return 1;
  let maxProduct = 0;
  for (let i = 0; i <= digits.length - span; i++) {
    let product = 1;
    for (let j = i; j < i + span; j++) {
      product *= Number(digits[j]);
    }
    if (product > maxProduct) maxProduct = product;
  }
  return maxProduct;
}

