exercises.starterCode:
function evaluate(instructions) {
  // your code here
}

exercises.solution:
function evaluate(instructions) {
  const stack = [];
  const defs = {};
  function exec(tokens) {
    let i = 0;
    while (i < tokens.length) {
      const token = tokens[i].toUpperCase();
      if (token === ":") {
        i++;
        const name = tokens[i].toUpperCase();
        if (!isNaN(Number(name))) throw new Error("Cannot redefine numbers");
        i++;
        const body = [];
        while (tokens[i] !== ";" && i < tokens.length) {
          body.push(tokens[i]);
          i++;
        }
        defs[name] = expandDef(body);
        i++;
        continue;
      }
      if (defs[token]) {
        exec(defs[token]);
      } else if (!isNaN(Number(token))) {
        stack.push(Number(token));
      } else if (token === "+" || token === "-" || token === "*" || token === "/") {
        if (stack.length < 2) throw new Error("Stack underflow");
        const b = stack.pop();
        const a = stack.pop();
        if (token === "+") stack.push(a + b);
        else if (token === "-") stack.push(a - b);
        else if (token === "*") stack.push(a * b);
        else { if (b === 0) throw new Error("Division by zero"); stack.push(Math.trunc(a / b)); }
      } else if (token === "DUP") {
        if (stack.length < 1) throw new Error("Stack underflow");
        stack.push(stack[stack.length - 1]);
      } else if (token === "DROP") {
        if (stack.length < 1) throw new Error("Stack underflow");
        stack.pop();
      } else if (token === "SWAP") {
        if (stack.length < 2) throw new Error("Stack underflow");
        const b = stack.pop();
        const a = stack.pop();
        stack.push(b);
        stack.push(a);
      } else if (token === "OVER") {
        if (stack.length < 2) throw new Error("Stack underflow");
        stack.push(stack[stack.length - 2]);
      } else {
        throw new Error("Unknown word: " + token);
      }
      i++;
    }
  }
  function expandDef(body) {
    const expanded = [];
    for (const t of body) {
      const upper = t.toUpperCase();
      if (defs[upper]) {
        expanded.push(...defs[upper]);
      } else {
        expanded.push(t);
      }
    }
    return expanded;
  }
  for (const line of instructions) {
    const tokens = line.split(/\s+/).filter(t => t.length > 0);
    exec(tokens);
  }
  return stack;
}

