exercises.solution:
function sumAll(range) {
  const [a, b] = range;
  if (typeof a !== "number" || typeof b !== "number" || a < 0 || b < 0) return "ERROR";
  const lo = Math.min(a, b);
  const hi = Math.max(a, b);
  let sum = 0;
  for (let i = lo; i <= hi; i++) sum += i;
  return sum;
}

