exercises.solution:
function permutations(str) {
  if (str.length <= 1) return [str];
  const result = [];
  for (let i = 0; i < str.length; i++) {
    const char = str[i];
    const remaining = str.slice(0, i) + str.slice(i + 1);
    const perms = permutations(remaining);
    for (const perm of perms) {
      result.push(char + perm);
    }
  }
  return result;
}

