function statBlock(str, dex, int, wis) {
  // Your code here

}

exercises.solution:
function statBlock(str, dex, int, wis) {
  const stats = { STR: str, DEX: dex, INT: int, WIS: wis };
  let strongest = 'DEX';
  for (const key in stats) {
    if (stats[key] > stats[strongest] || (stats[key] === stats[strongest] && key < strongest)) {
      strongest = key;
    }
  }
  return { stats, strongest };
}

