exercises.starterCode:
function bestSpell(spells, mana, enemyWeakness) {
  // Your code here

}

exercises.solution:
function bestSpell(spells, mana, enemyWeakness) {
  const affordable = spells.filter(s => s.manaCost <= mana);
  const matching = affordable.filter(s => s.element === enemyWeakness);
  if (matching.length === 0) return null;
  return matching.reduce((best, s) => s.damage > best.damage ? s : best);
}

