exercises.solution:
function renderRow(row) {
  const legend = { '#': 'wall', '.': 'floor', 'P': 'player', 'G': 'gold', 'M': 'monster', 'D': 'door' };
  return row.split('').map(ch => ({ char: ch, tile: legend[ch] || 'unknown' }));
