function monsterManual(monsters) {
  const byType = {};
  let strongest = monsters[0];
  let totalXP = 0;

  for (const m of monsters) {
    if (!byType[m.type]) byType[m.type] = [];
    byType[m.type].push(m.name);
    if (m.hp > strongest.hp) strongest = m;
    totalXP += m.xp;
  }

  return { byType, strongest, totalXP, uniqueTypes: Object.keys(byType).length };
}

