exercises.solution:
function parseTerrain(terrain) {
  const platforms = [];
  const hazards = [];
  let coins = 0;
  let i = 0;

  while (i < terrain.length) {
    if (terrain[i] === '_') {
      const start = i;
      while (i < terrain.length && terrain[i] === '_') i++;
      platforms.push({ start, length: i - start });
    } else {
      if (terrain[i] === '^') hazards.push(i);
      if (terrain[i] === 'C') coins++;
      i++;
    }
  }

  return { platforms, hazards, coins };
}

