exercises.solution:
class Spell {
  constructor(name, element, damage, manaCost, cooldown) {
    this.name = name;
    this.element = element;
    this.damage = damage;
    this.manaCost = manaCost;
    this.cooldown = cooldown;
    this.ready = true;
    this.turnsLeft = 0;
  }

  cast(casterMana) {
    if (!this.ready) return { success: false, reason: 'not ready' };
    if (casterMana < this.manaCost) return { success: false, reason: 'not enough mana' };
    this.ready = false;
    this.turnsLeft = this.cooldown;
    return { success: true, damage: this.damage, manaCost: this.manaCost };
  }

  tick() {
    if (!this.ready) {
      this.turnsLeft--;
      if (this.turnsLeft <= 0) this.ready = true;
    }
  }
}

class SpellBook {
  constructor() {
    this.spells = [];
  }

  add(spell) {
    this.spells.push(spell);
  }

  castByName(name, casterMana) {
    const spell = this.spells.find(s => s.name === name);
    if (!spell) return { success: false, reason: 'unknown spell' };
    return spell.cast(casterMana);
  }

  tickAll() {
    this.spells.forEach(s => s.tick());
  }

  available(mana) {
    return this.spells.filter(s => s.ready && s.manaCost <= mana).map(s => s.name);
  }
}

