class Quest {
  constructor(name, objectives) {
    this.name = name;
    this.objectives = objectives;
    this.progress = objectives.map(() => false);
  }

  complete(index) {
    this.progress[index] = true;
  }

  get isComplete() {
    return this.progress.every(p => p);
  }

  get percentComplete() {
    const done = this.progress.filter(p => p).length;
    return Math.floor((done / this.progress.length) * 100);
  }
}

class QuestLog {
  constructor() {
    this.quests = [];
  }

  add(quest) {
    this.quests.push(quest);
  }

  active() {
    return this.quests.filter(q => !q.isComplete).map(q => q.name);
  }

  completed() {
    return this.quests.filter(q => q.isComplete).map(q => q.name);
  }

  summary() {
    const comp = this.quests.filter(q => q.isComplete).length;
    return { total: this.quests.length, completed: comp, active: this.quests.length - comp };
  }
}

