exercises.solution:
function analyzeWorld(mapStr) {
  const rows = mapStr.split('\n');
  const platforms = [];
  const coins = [];
  const enemies = [];
  let playerStart = null;
  let goal = null;

  for (let r = 0; r < rows.length; r++) {
    let c = 0;
    while (c < rows[r].length) {
      const ch = rows[r][c];
      if (ch === '_') {
        const start = c;
        while (c < rows[r].length && rows[r][c] === '_') c++;
        platforms.push({ row: r, start, length: c - start });
      } else {
        if (ch === 'C') coins.push({ row: r, col: c });
        if (ch === 'E') enemies.push({ row: r, col: c });
        if (ch === 'P') playerStart = { row: r, col: c };
        if (ch === 'G') goal = { row: r, col: c };
        c++;
      }
    }
  }

  const gaps = [];
  const bottom = rows[rows.length - 1];
  let bc = 0;
  while (bc < bottom.length) {
    if (bottom[bc] === '.') {
      const start = bc;
      while (bc < bottom.length && bottom[bc] === '.') bc++;
      gaps.push({ row: rows.length - 1, start, length: bc - start });
    } else {
      bc++;
    }
  }

  return { platforms, coins, enemies, playerStart, goal, gaps };
}

