exercises.solution:
function createDungeon(mapStr) {
  const map = mapStr.split('\n').map(r => r.split(''));
  let player = { row: 0, col: 0, hp: 100, keys: 0 };
  let won = false;
  let lost = false;
  const log = [];

  for (let r = 0; r < map.length; r++) {
    for (let c = 0; c < map[r].length; c++) {
      if (map[r][c] === 'P') {
        player.row = r;
        player.col = c;
        map[r][c] = '.';
      }
    }
  }

  const dirs = { up: [-1,0], down: [1,0], left: [0,-1], right: [0,1] };

  function getState() {
    return { player: { ...player }, map: map.map(r => [...r]), won, lost, log: [...log] };
  }

  function move(direction) {
    if (won || lost) return getState();
    const [dr, dc] = dirs[direction];
    const nr = player.row + dr;
    const nc = player.col + dc;

    if (nr < 0 || nr >= map.length || nc < 0 || nc >= map[0].length) {
      log.push('Blocked by wall');
      return getState();
    }

    const tile = map[nr][nc];

    if (tile === '#') {
      log.push('Blocked by wall');
      return getState();
    }

    if (tile === 'D') {
      if (player.keys > 0) {
        player.keys--;
        map[nr][nc] = '.';
        log.push('Opened door');
      } else {
        log.push('Door is locked');
        return getState();
      }
    }

    player.row = nr;
    player.col = nc;
    log.push('Moved ' + direction);

    if (tile === 'K') {
      player.keys++;
      map[nr][nc] = '.';
      log.push('Collected key');
    } else if (tile === 'T') {
      player.hp -= 10;
      map[nr][nc] = '.';
      log.push('Hit trap! -10 HP');
    } else if (tile === 'M') {
      player.hp -= 5;
      map[nr][nc] = '.';
      log.push('Defeated monster! -5 HP');
    } else if (tile === 'G') {
      won = true;
      log.push('Reached the goal!');
    }

    if (player.hp <= 0) {
      lost = true;
      log.push('You have fallen!');
    }

    return getState();
  }

  return { getState, move };
}

