  // Your code here

}

exercises.solution:
function createLevel(mapStr) {
  const map = mapStr.split('\n').map(r => r.split(''));
  const mapHeight = map.length;
  const mapWidth = map[0].length;
  let player = { x: 0, y: 0, vy: 0, grounded: false, coins: 0 };
  let cameraX = 0;
  let won = false;
  let ticks = 0;

  for (let r = 0; r < mapHeight; r++) {
    for (let c = 0; c < mapWidth; c++) {
      if (map[r][c] === 'P') {
        player.x = c;
        player.y = r;
        map[r][c] = '.';
      }
    }
  }

  function isSolid(x, y) {
    if (y >= mapHeight) return true;
    if (x < 0 || x >= mapWidth || y < 0) return false;
    return map[y][x] === '_';
  }

  function getState() {
    return { player: { ...player }, cameraX, won, ticks };
  }

  function tick(input) {
    if (won) return getState();

    if (input.right) player.x += 1;
    if (input.left) player.x -= 1;
    player.x = Math.max(0, Math.min(mapWidth - 1, player.x));

    if (input.jump && player.grounded) {
      player.vy = -2;
      player.grounded = false;
    }

    player.vy += 1;
    player.y += player.vy;

    if (player.y >= mapHeight) {
      player.y = mapHeight - 1;
      player.vy = 0;
      player.grounded = true;
    } else if (isSolid(player.x, player.y)) {
      if (player.vy > 0) {
        while (isSolid(player.x, player.y)) player.y--;
      } else {
        while (isSolid(player.x, player.y)) player.y++;
      }
      player.vy = 0;
      player.grounded = true;
    } else if (isSolid(player.x, player.y + 1)) {
      player.grounded = true;
    } else {
      player.grounded = false;
    }

    if (player.y >= 0 && player.y < mapHeight && player.x >= 0 && player.x < mapWidth) {
      const tile = map[player.y][player.x];
      if (tile === 'C') {
        player.coins++;
        map[player.y][player.x] = '.';
      } else if (tile === 'G') {
        won = true;
      }
    }

    cameraX = Math.max(0, player.x - 3);
    ticks++;

    return getState();
  }

  return { getState, tick };
}

