exercises.solution:
function createQuestEngine(config) {
  const map = config.mapStr.split('\n').map(r => r.split(''));
  const state = {
    name: config.heroName,
    heroClass: config.heroClass,
    hp: 100,
    maxHp: 100,
    level: 1,
    xp: 0,
    gold: config.startingGold,
    inventory: ['Wooden Sword'],
    row: 0,
    col: 0,
    questComplete: false,
    alive: true
  };

  for (let r = 0; r < map.length; r++) {
    for (let c = 0; c < map[r].length; c++) {
      if (map[r][c] === 'P') { state.row = r; state.col = c; map[r][c] = '.'; }
    }
  }

  function gainXP(amount) {
    state.xp += amount;
    while (state.xp >= 100) {
      state.xp -= 100;
      state.level++;
      state.maxHp += 20;
      state.hp = state.maxHp;
    }
  }

  function hero() {
    return {
      name: state.name,
      heroClass: state.heroClass,
      hp: state.hp,
      maxHp: state.maxHp,
      level: state.level,
      xp: state.xp,
      gold: state.gold,
      inventory: [...state.inventory]
    };
  }

  const dirs = { up: [-1,0], down: [1,0], left: [0,-1], right: [0,1] };

  function explore(direction) {
    if (!state.alive || state.questComplete) return { event: 'blocked' };
    const [dr, dc] = dirs[direction];
    const nr = state.row + dr;
    const nc = state.col + dc;

    if (nr < 0 || nr >= map.length || nc < 0 || nc >= map[0].length || map[nr][nc] === '#') {
      return { event: 'blocked' };
    }

    const tile = map[nr][nc];

    if (tile === 'D') {
      const keyIdx = state.inventory.indexOf('Dungeon Key');
      if (keyIdx === -1) return { event: 'blocked' };
      state.inventory.splice(keyIdx, 1);
      map[nr][nc] = '.';
    }

    state.row = nr;
    state.col = nc;

    if (tile === 'M') {
      state.hp -= 5;
      state.gold += 10;
      gainXP(25);
      map[nr][nc] = '.';
      if (state.hp <= 0) { state.alive = false; return { event: 'defeat', message: 'You have fallen!' }; }
      return { event: 'combat', enemy: 'Monster', hpLost: 5, xpGained: 25, goldGained: 10 };
    }

    if (tile === 'K') {
      state.inventory.push('Dungeon Key');
      map[nr][nc] = '.';
      return { event: 'key', message: 'Found a dungeon key!' };
    }

    if (tile === 'T') {
      state.hp -= 10;
      map[nr][nc] = '.';
      if (state.hp <= 0) { state.alive = false; return { event: 'defeat', message: 'You have fallen!' }; }
      return { event: 'trap', hpLost: 10 };
    }

    if (tile === 'G') {
      state.questComplete = true;
      return { event: 'victory', message: 'Quest complete!' };
    }

    return { event: 'move' };
  }

  function buy(itemName, price) {
    if (state.gold >= price) {
      state.gold -= price;
      state.inventory.push(itemName);
      return true;
    }
    return false;
  }

  function status() {
    return { hero: hero(), questComplete: state.questComplete, alive: state.alive };
  }

  return { hero, explore, buy, status };
}
